import {useEffect, useState, } from 'react'
import { useNavigate,Link } from 'react-router-dom';

import { api } from '../../service/';
import Header from "../../components/Header/Header";
import Container  from 'react-bootstrap/Container';
import Col  from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row'
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Produto from '../../components/Produto/Produto';







export function About(){

    const [listaProduto,setListaProduto] = useState([])

    const getProduto = async () =>{
        var response = await api.get('/produto/all')
        // setListaProduto(response.data)
        console.log(response)
   
    }
     
  
    useEffect(() => {
        api
          .get("/produto/all")
          .then((response) => setListaProduto(response))
          .catch((err) => {
            console.error("ops! ocorreu um erro" + err);
          });
      }, []);


    return (<>

        <Header></Header>

        <h1>Teste</h1>
        <Container className="mt-5 p-2">
            <Row>
                <Col className="p-5">
                    <Row xs={2} md={3} className="g-4 p-2">
                        {
                            listaProduto.map( o => 
                            {      return (
                                <Col>
                                    <Card style={{ width: '18rem' }}>
                                        <Card.Img variant="top" src={o.imagemUrl} />
                                        <Card.Body>
                                        <Card.Title>{o.nome}</Card.Title>
                                        <Card.Text>
                                            <p>Descrição: </p>
                                            {o.descricao}
                                        </Card.Text>
                                        <Button variant="primary">Ok</Button>
                                        </Card.Body>
                                    </Card>
                                    </Col>
                                )}
                            )}
                    </Row>
                </Col>             
            </Row>
            





        </Container>

    
       
    </>)
}
